from pprint import pprint
import inspect
        
class Printer:
    def ready(variable_name, variable_value, the_object=False):
        

        stack = inspect.stack()
        the_class = stack[1][0].f_locals["self"].__class__.__name__
        the_method = stack[1][0].f_code.co_name
        the_line = stack[1][0].f_code.co_firstlineno

        print(f"\n========================== In {the_class}.{the_method} on line {the_line} ==========================\n")

        if the_object:
            print(f"self is {the_object}")
        
        print(f"The variable is `  {variable_name}  `. Its value is `  ")
        pprint(variable_value)
        print("  `")

    def shout(msg=False):
        stack = inspect.stack()
        the_class = stack[1][0].f_locals["self"].__class__.__name__
        the_method = stack[1][0].f_code.co_name
        the_line = stack[1][0].f_code.co_firstlineno
        
        print(f"\n========================== Currently at {the_class}.{the_method} on line {the_line} ==========================\n")
        
        if msg:
            pprint(msg)